package fundamentos;

// TODO: Auto-generated Javadoc
/**
 * The Class ConversaoNumerica.
 */
public class ConversaoNumerica {

	/**
	 * The main method.
	 * @author eduardoaf
	 * @param args the arguments
	 */
	public static void main(String[] args) {
	//conversao explicita
		float f = (float) 0.1;
		System.out.println(f);
		
		//conversao explicita
		int i1 = (int)7.9;
		System.out.println(i1);
		
		//conversao implicita
		int i2 = 'a';
		System.out.println(i2);
		
		//conversao implicita
		double d = 1000;
		System.out.println(d);
		

	}

}
